mapper.select:
SELECT MIN(A.STD_DT) MIN, MAX(A.STD_DT) MAX
		 FROM JISUDEV.RES_STYLE_IDX A
		 WHERE A.U_CD = #{u_cd}		
		 AND A.SIMUL_ID = #{simul_id}

SELECT A.CLS_PRC CLS_PRC, B.CLS_PRC CLS_PRC_BM
		FROM JISUDEV.RES_STYLE_IDX A, FNS_UD B
		WHERE A.STD_DT = #{trd_dt}
		AND A.U_CD = #{u_cd}
		AND A.STD_DT = B.TRD_DT
		AND B.U_CD = #{u_cd_bm}
		AND A.SIMUL_ID = #{simul_id}

SELECT A.STD_DT TRD_DT, A.CLS_PRC CLS_PRC, B.CLS_PRC CLS_PRC_BM
		FROM JISUDEV.RES_STYLE_IDX A, FNS_UD B
		WHERE (A.STD_DT = #{w1} OR A.STD_DT = #{m1} OR A.STD_DT = #{m3} OR A.STD_DT = #{m6} OR A.STD_DT = #{y1} OR A.STD_DT = #{y3} OR A.STD_DT = #{y5} OR A.STD_DT = #{total})
		AND A.SIMUL_ID = #{simul_id} 
		AND A.U_CD = #{u_cd}
		AND A.STD_DT = B.TRD_DT
		AND B.U_CD = #{u_cd_bm}
		ORDER BY A.STD_DT DESC

SELECT A.CLS_PRC CLS_PRC
		FROM FNS_UD A
		WHERE A.TRD_DT = #{trd_dt}
		AND A.U_CD = #{u_cd}

SELECT A.CLS_PRC CLS_PRC
		FROM JISUDEV.RES_STYLE_IDX A
		WHERE A.STD_DT = #{trd_dt}
		AND A.SIMUL_ID = #{simul_id}
		AND A.U_CD = #{u_cd}


SELECT U_NM "지수이름"
FROM FNS_U_MAST
WHERE U_CD = #{u_cd}

SELECT A.YMD, A.CLS_PRC CLS_PRC, A.YIELD/100 YIELD, B.CLS_PRC CLS_PRC_BM, B.YIELD/100 YIELD_BM
		FROM FNS_UW A, FNS_UW B
		WHERE A.U_CD = #{u_cd}
		AND B.U_CD = #{u_cd_bm}
		AND A.YMD BETWEEN #{t0} AND #{t1}
		AND A.YMD = B.YMD
		ORDER BY A.YMD ASC

SELECT B.YMD, A.CLS_PRC CLS_PRC, B.CLS_PRC CLS_PRC_BM, C.AMOUNT/100/12 RF
		FROM JISUDEV.RES_STYLE_IDX A, FNS_UM B, FNE_ECO_DATA C
		WHERE A.U_CD = #{u_cd}
		AND A.SIMUL_ID = #{simul_id}
		AND B.U_CD = #{u_cd_bm}
		AND A.STD_DT = B.END_DT 
		AND C.ECO_CD = '11.02.003.001'
		AND A.STD_DT = C.TRD_DT
		AND B.YMD BETWEEN #{t0} AND #{t1}
		AND C.TERM = 'D'
		ORDER BY A.STD_DT ASC

SELECT A.STD_DT "날짜", A.CLS_PRC "종가"
		 FROM JISUDEV.RES_STYLE_IDX A
		 WHERE A.U_CD = #{u_cd}
		 AND A.SIMUL_ID = #{simul_id}
		 ORDER BY A.STD_DT ASC;
